import React from 'react';

function PollItem({ poll, onVote }) {
  return (
    <div className="PollItem">
      <h3>{poll.question}</h3>
      <ul>
        {poll.options.map((option, index) => (
          <li key={index}>
            {option} 
            <button onClick={() => onVote(index)}>Vote</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PollItem;
